    BuildingList(String name, int image, String body, String address){
    this.image = image;
    this.name = name;
    this.body = body;
    this.address = address;

    }

    public String getName(){
        return name;
    }

    public int getImage(){
        return image;
    }

    public String getBody(){
        return body;
    }

